    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // BOTON AGRGAR
        DefaultTableModel modelo= (DefaultTableModel) tabla.getModel();
        
          String genero = null;
        if (masculino.isSelected()){
            genero="masculino";
        }
        if (femenino.isSelected()){
            genero="femenino";
     
            }
        
        Object  [] fila=new Object [6];
        fila [0]= nom.getText();
        fila [1]= ape.getText();
        fila [2]= tel.getText();
        fila [3]= mail.getText();
        fila [4]= direc.getText();
         fila [5]= genero;
         
      
       
        modelo.addRow(fila);
        
        tabla.setModel(modelo);
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // BOTON ELIMINAR
        DefaultTableModel modelo= (DefaultTableModel) tabla.getModel();
        int a=tabla.getSelectedRow();
        if (a<0){
            JOptionPane.showConfirmDialog(null,"debe seleccionar una fila");
        }else{
            int confirmar=JOptionPane.showConfirmDialog(null,"esta seguro??");
            if (JOptionPane.OK_OPTION==confirmar){
                modelo.removeRow(a);
                JOptionPane.showMessageDialog(null,"Row eliminado");
            }
        }
        
        
    }//GEN-LAST:event_jButton2ActionPerformed

