    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
String mensaje=" ";
int total=0;
if(optsencilla.isSelected()){
    mensaje=mensaje+"sencilla,";
    total=total+150;
}
if(optdoble.isSelected()){
    mensaje=mensaje+"doble,";
    total=total+200;
}
if(optpremiere.isSelected()){
    mensaje=mensaje+"premiere,";
    
    total=total+300;
}
if(optnormal.isSelected()){
    mensaje=mensaje+"normal,";
    total=total+30;
}
if(optejecutivo.isSelected()){
    mensaje=mensaje+"ejecutivo,";
    total=total+40;
}

if(optbuffet.isSelected()){
    mensaje=mensaje+"buffet,";
    total=total+60;
}
if(optgym.isSelected()){
    mensaje=mensaje+"gym,";
    total=total+100;
}
if(optwifi.isSelected()){
    mensaje=mensaje+"wifi,";
    total=total+150;
}
serv.setText(mensaje);
optmonto.setText(String.valueOf(total));

    }//GEN-LAST:event_jButton1ActionPerformed

