    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    String nume1,nume2,nume3,nume4,nume5;
    int total,n1,n2,n3,n4,n5;
        
     DefaultTableModel modelo= (DefaultTableModel) tabla.getModel();

      tabla.getModel();
      nume1=num1.getText();
      nume2=num2.getText();
      nume3=num3.getText();
      nume4=num4.getText();
      nume5=num5.getText();
      
      n1=Integer.parseInt(nume1);
      n2=Integer.parseInt(nume2);
      n3=Integer.parseInt(nume3);
      n4=Integer.parseInt(nume5);
      n5=Integer.parseInt(nume1);
      
      total=n1+n2+n3+n4+n5;
      
      
      Object  [] fila=new Object [6];
       fila [0]= n1;
       fila [1]= n2;
       fila [2]= n3;
       fila [3]= n4;
       fila [4]=n5;
       fila [5]=String.valueOf(total);
       
        modelo.addRow(fila);
        tabla.setModel(modelo);       
    }//GEN-LAST:event_jButton1ActionPerformed

