    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String te,esp="",par="",o="",newpal="";
        char ch,ch2;
        int i,longi,j=0;
        te=parra.getText();
       longi=te.length();
       if (porcentaje.isSelected()){
            
       for (i=0;i<longi;i=i+2){
           ch=par.charAt(i);
           j=i+1;
           ch2=par.charAt(j);
           if (ch!=' '){
               newpal=newpal+ch;      
               }
            if (ch==' '){
             newpal=newpal+"%";
            
            }
         if (ch2!=' '){
               newpal=newpal+ch2;      
               }
            if (ch2==' '){
             newpal=newpal+"%";
            
            }
            
        }
           
       }
       
       resul.setText(newpal);
    }//GEN-LAST:event_jButton1ActionPerformed

