    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // CODIFICADOR
String parrafo,newpal=" ";
    parrafo=parra.getText();
    int i;
   
    int x;
    char ch;
    x=parrafo.length();
    for(i=0;i<x;i=i+1){
        ch=parrafo.charAt(i);
        
        if(ch=='1'){
            newpal=newpal+"a";
     
        }
    
      if(ch=='2'){
           newpal=newpal+"b";
          
      }
       if(ch!='1'&& ch!='2'&&ch!='3'&&ch!='4'&&ch!='5'&&ch!='6'&&ch!='7'&&ch!='8'&&ch!='9'&&ch!='0' ){
            newpal=newpal+ch;      
             }
           
       if(ch=='3'){
           newpal=newpal+"c";
            
      }
        
      if(ch=='4'){
           newpal=newpal+"d";
          
      }
      
      
       if(ch=='5'){
         newpal=newpal+"e";
             
      }
             if(ch=='6'){
             newpal=newpal+"f";
     
        }
    
      if(ch=='7'){
         newpal=newpal+"g";
          
      }
           
       if(ch=='8'){
           newpal=newpal+"h";
            
      }
        
      if(ch=='9'){
           newpal=newpal+"i";
          
      }
      
      
       if(ch=='0'){
           newpal=newpal+"j";
             
      }
    
    resul.setText(newpal);
    
        
    }
    
    }//GEN-LAST:event_jButton1ActionPerformed
