    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        DefaultTableModel modelo= (DefaultTableModel) tabla.getModel();
        int anio,edad,total;
        float s,h,i,j,k;
        String nombrecompleto="",n,nn,a,aa;
        String carnet,nota1,nota2,nota3;
         
        carnet=car.getText();
        n=primero.getText();
        nn=segundo.getText();
        a=ape1.getText();
        aa=ape2.getText();
        nota1=not1.getText();
        
    nombrecompleto=nombrecompleto+n+" "+nn+" "+a+" "+aa;
        
   edad=Integer.parseInt(nacimiento.getText());
   anio=2019-edad;
   
 
   
   h=Float.parseFloat(not1.getText());
   i=Float.parseFloat(not2.getText());
   j=Float.parseFloat(not3.getText());
 s=h+i+j;
 k=s/3;
        Object  [] fila=new Object [8];
        fila [0]= carnet;
        fila [1]= nombrecompleto;
        
        fila [2]=String.valueOf(anio);
        fila [3]= not1.getText();
        fila [4]= not2.getText();
         fila [5]=not3.getText();
         fila [6]= s;
         fila [7]= k;
        
        
  modelo.addRow(fila);
 tabla.setModel(modelo); 



        
    }//GEN-LAST:event_jButton1ActionPerformed

