    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

     String parrafo,numero=" ",longitud=" ",letra="";
     int i,longi,e=0;
     char ch;
     parrafo=parra.getText();
     longi=parrafo.length();
     for (i=0;i<longi;i=i+1){
         ch=parrafo.charAt(i);
         if(ch=='1'|| ch=='2'||ch=='3'||ch=='4'||ch=='5'||ch=='6'||ch=='7'||ch=='8'||ch=='9' ){
            numero=numero+ch;      
             }
             else if(ch!='1'&& ch!='2'&& ch!='3'&& ch!='4'&&ch!='5'||ch=='6'||ch=='7'||ch=='8'||ch=='9'){
                 longitud=longitud+ch;
                
         }
         else if(ch!='a'&& ch!='e'&& ch!='i'&& ch!='o'&&ch!='u'){
                 letra=letra+ch;
         }
           
     
     }
        num.setText(String.valueOf(numero));
        lon.setText(longitud);
        let.setText (letra);
     
        
    }//GEN-LAST:event_jButton1ActionPerformed

