    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
       String text,pal=" ";
       int i, longi;
       char ch;
       text=wl.getText();
       longi=text.length();
       for(i=0;i<longi;i=i+1){
           ch=text.charAt(i);
           switch (ch){
           case ' ':pal=pal+"";break;
            case 'a':pal=pal+"*";break;
            case 'e':pal=pal+"#";break;
            case 'i':pal=pal+"%";break;
            case 'o':pal=pal+"/";break;
            case 'u':pal=pal+"$";break;
              default:pal=pal+ch; break;  
       }
       }
 
         resul.setText(String.valueOf(pal));  
    }//GEN-LAST:event_jButton1ActionPerformed

