    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
String val;
float valor,total=0;
val=mensu.getText();

valor=Float.parseFloat(val);

if (optiva.isSelected()){
    total=(float)(valor*0.1);
}
if(optisr.isSelected()){
    total=(float)(valor*0.078);
}

if(optimpuesto.isSelected()){
    total=(float)(valor*0.6);
}
resul.setText(String.valueOf(total));

        
    }//GEN-LAST:event_jButton1ActionPerformed

