    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        DefaultTableModel modelo= (DefaultTableModel) tabla.getModel();
        int anio,edad;
        float extra,extra2,sal,s,h,salex;
        String nombrecompleto="",n,nn,a,aa;
        
        n=primero.getText();
        nn=segundo.getText();
        a=ape1.getText();
        aa=ape2.getText();
    nombrecompleto=nombrecompleto+n+" "+nn+" "+a+" "+aa;
        
   edad=Integer.parseInt(nacimiento.getText());
   anio=2019-edad;
   s=Float.parseFloat(sala.getText());
   h=Float.parseFloat(extras.getText());
   extra=(float)(s*0.01);
   extra2=s*h;
   salex=s+extra2;
        
       
        Object  [] fila=new Object [5];
        fila [0]= nombrecompleto;
        fila [1]= String.valueOf(anio);
        fila [2]= sala.getText();
        fila [3]=  String.valueOf(extra);
         fila [4]=String.valueOf(salex);
        
  modelo.addRow(fila);
 tabla.setModel(modelo); 
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        
         DefaultTableModel modelo= (DefaultTableModel) tabla.getModel();
        int a=tabla.getSelectedRow();
        if (a<0){
            JOptionPane.showConfirmDialog(null,"debe seleccionar una fila");
        }else{
            int confirmar=JOptionPane.showConfirmDialog(null,"esta seguro??");
            if (JOptionPane.OK_OPTION==confirmar){
                modelo.removeRow(a);
                JOptionPane.showMessageDialog(null,"Row eliminado");
            }
        }
    }//GEN-LAST:event_jButton2ActionPerformed

