    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
 
        String n;
        int i,psum=0,isum=0,valor,longi;
        n=val.getText();
        valor=Integer.parseInt(n);
        
        if (optpar.isSelected()){
        for(i=0;i<=valor;i=i+2){
            psum=psum +i;
        }  
         resul.setText(String.valueOf(psum));
        }
        
        
          if (optimp.isSelected()){
              for (i=1;i<=valor;i=i+2){
                isum=isum+i;
             }
              resul.setText(String.valueOf(isum));
          }
        
        
       
    }//GEN-LAST:event_jButton1ActionPerformed

