    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

     DefaultTableModel modelo= (DefaultTableModel) tabla.getModel();
     String parrafo;
     int i,longi,aletra=0,eletra=0,iletra=0,oletra=0,uletra=0;
     char ch;
     parrafo=parra.getText();
     
     longi=parrafo.length();
     for (i=0;i<longi;i=i+1){
         ch=parrafo.charAt(i);
        
         if (ch=='a'){
             aletra=aletra+1;
         }
         if (ch=='e'){
 eletra=eletra+1;
         }
         if (ch=='i'){
 iletra=iletra+1;
         }
         if (ch=='o'){
 oletra=oletra+1;
         }
         if (ch=='u'){
 uletra=uletra+1;
         }
      }
  Object  [] fila=new Object [5];
     
        fila [0]= String.valueOf(aletra);
        fila [1]= String.valueOf(eletra);
        fila [2]= String.valueOf(iletra);
        fila [3]=  String.valueOf(oletra);
         fila [4]=String.valueOf(uletra);
  modelo.addRow(fila);
 tabla.setModel(modelo); 

    }//GEN-LAST:event_jButton1ActionPerformed

